ZOO_LOG_DIR="{{ zookeeper_log_dir }}"
JVMFLAGS="-Dzookeeper.jdk.tls.rejectClientInitiatedRenegotiation=true -Dzookeeper.serverCnxnFactory=org.apache.zookeeper.server.NettyServerCnxnFactory -Dzookeeper.clientCnxnSocket=org.apache.zookeeper.ClientCnxnSocketNetty -Dzookeeper.ssl.keyStore.location={% if keystore_file is defined %}{{ keystore_file }}{% else %}/etc/ssl/private/{{ inventory_hostname }}.jks{% endif %} -Dzookeeper.ssl.keyStore.password={{ keystore_password }} -Dzookeeper.ssl.trustStore.location={% if truststore_file is defined %}{{ truststore_file }}{% else %}/etc/ssl/certs/truststore.jks{% endif %} -Dzookeeper.ssl.trustStore.password={{ truststore_password }} -Dzookeeper.client.secure=true"
